  `khauphan` int(11) NOT NULL,
  `thoigianchuanbi` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `capdo` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `nguyenlieu` text COLLATE utf8mb4_unicode_ci NOT NULL,
